n0:createOracleTrigger.n0:source:
CREATE OR REPLACE EDITIONABLE TRIGGER "CHINOOK"."T$ALBUM" 
	after insert or update or delete on CHINOOK.ALBUM
	for each row
	declare
		V_FLAG	VARCHAR(1);
		V_ALBUMID	NUMBER;
	begin
		if inserting then
			V_ALBUMID := :new.ALBUMID;
			V_FLAG := 'I';
		end if;

		if updating then
			V_ALBUMID := :new.ALBUMID;
			V_FLAG := 'I';
		end if;

		if deleting then
			V_ALBUMID := :old.ALBUMID;
			V_FLAG := 'D';
		end if;

		insert into CHINOOK.J$ALBUM
		(
			JRN_SUBSCRIBER,
			JRN_CONSUMED,
			JRN_FLAG,
			JRN_DATE,
			ALBUMID
		)
		select	JRN_SUBSCRIBER,
			'0',
			V_FLAG,
			sysdate,
			V_ALBUMID
		from	CHINOOK.SNP_SUBSCRIBERS
		where	JRN_TNAME = 'CHINOOK.ALBUM'
		/* The following line can be uncommented for symetric replication */
		/* and	upper(USER) <> upper('CHINOOK') */
		;
	end;


//
ALTER TRIGGER "CHINOOK"."T$ALBUM" ENABLE;
