n0:createOracleTrigger.n0:source:
CREATE OR REPLACE EDITIONABLE TRIGGER "CHINOOK"."T$ARTIST" 
	after insert or update or delete on CHINOOK.ARTIST
	for each row
	declare
		V_FLAG	VARCHAR(1);
		V_ARTISTID	NUMBER;
	begin
		if inserting then
			V_ARTISTID := :new.ARTISTID;
			V_FLAG := 'I';
		end if;

		if updating then
			V_ARTISTID := :new.ARTISTID;
			V_FLAG := 'I';
		end if;

		if deleting then
			V_ARTISTID := :old.ARTISTID;
			V_FLAG := 'D';
		end if;

		insert into CHINOOK.J$ARTIST
		(
			JRN_SUBSCRIBER,
			JRN_CONSUMED,
			JRN_FLAG,
			JRN_DATE,
			ARTISTID
		)
		select	JRN_SUBSCRIBER,
			'0',
			V_FLAG,
			sysdate,
			V_ARTISTID
		from	CHINOOK.SNP_SUBSCRIBERS
		where	JRN_TNAME = 'CHINOOK.ARTIST'
		/* The following line can be uncommented for symetric replication */
		/* and	upper(USER) <> upper('CHINOOK') */
		;
	end;


//
ALTER TRIGGER "CHINOOK"."T$ARTIST" ENABLE;
