n0:createOracleTrigger.n0:source:
CREATE OR REPLACE EDITIONABLE TRIGGER "CHINOOK"."T$CUSTOMER" 
	after insert or update or delete on CHINOOK.CUSTOMER
	for each row
	declare
		V_FLAG	VARCHAR(1);
		V_CUSTOMERID	NUMBER;
	begin
		if inserting then
			V_CUSTOMERID := :new.CUSTOMERID;
			V_FLAG := 'I';
		end if;

		if updating then
			V_CUSTOMERID := :new.CUSTOMERID;
			V_FLAG := 'I';
		end if;

		if deleting then
			V_CUSTOMERID := :old.CUSTOMERID;
			V_FLAG := 'D';
		end if;

		insert into CHINOOK.J$CUSTOMER
		(
			JRN_SUBSCRIBER,
			JRN_CONSUMED,
			JRN_FLAG,
			JRN_DATE,
			CUSTOMERID
		)
		select	JRN_SUBSCRIBER,
			'0',
			V_FLAG,
			sysdate,
			V_CUSTOMERID
		from	CHINOOK.SNP_SUBSCRIBERS
		where	JRN_TNAME = 'CHINOOK.CUSTOMER'
		/* The following line can be uncommented for symetric replication */
		/* and	upper(USER) <> upper('CHINOOK') */
		;
	end;


//
ALTER TRIGGER "CHINOOK"."T$CUSTOMER" ENABLE;
