n0:createOracleTrigger.n0:source:
CREATE OR REPLACE EDITIONABLE TRIGGER "CHINOOK"."T$EMPLOYEE" 
	after insert or update or delete on CHINOOK.EMPLOYEE
	for each row
	declare
		V_FLAG	VARCHAR(1);
		V_EMPLOYEEID	NUMBER;
	begin
		if inserting then
			V_EMPLOYEEID := :new.EMPLOYEEID;
			V_FLAG := 'I';
		end if;

		if updating then
			V_EMPLOYEEID := :new.EMPLOYEEID;
			V_FLAG := 'I';
		end if;

		if deleting then
			V_EMPLOYEEID := :old.EMPLOYEEID;
			V_FLAG := 'D';
		end if;

		insert into CHINOOK.J$EMPLOYEE
		(
			JRN_SUBSCRIBER,
			JRN_CONSUMED,
			JRN_FLAG,
			JRN_DATE,
			EMPLOYEEID
		)
		select	JRN_SUBSCRIBER,
			'0',
			V_FLAG,
			sysdate,
			V_EMPLOYEEID
		from	CHINOOK.SNP_SUBSCRIBERS
		where	JRN_TNAME = 'CHINOOK.EMPLOYEE'
		/* The following line can be uncommented for symetric replication */
		/* and	upper(USER) <> upper('CHINOOK') */
		;
	end;


//
ALTER TRIGGER "CHINOOK"."T$EMPLOYEE" ENABLE;
