n0:createOracleTrigger.n0:source:
CREATE OR REPLACE EDITIONABLE TRIGGER "CHINOOK"."T$GENRE" 
	after insert or update or delete on CHINOOK.GENRE
	for each row
	declare
		V_FLAG	VARCHAR(1);
		V_GENREID	NUMBER;
	begin
		if inserting then
			V_GENREID := :new.GENREID;
			V_FLAG := 'I';
		end if;

		if updating then
			V_GENREID := :new.GENREID;
			V_FLAG := 'I';
		end if;

		if deleting then
			V_GENREID := :old.GENREID;
			V_FLAG := 'D';
		end if;

		insert into CHINOOK.J$GENRE
		(
			JRN_SUBSCRIBER,
			JRN_CONSUMED,
			JRN_FLAG,
			JRN_DATE,
			GENREID
		)
		select	JRN_SUBSCRIBER,
			'0',
			V_FLAG,
			sysdate,
			V_GENREID
		from	CHINOOK.SNP_SUBSCRIBERS
		where	JRN_TNAME = 'CHINOOK.GENRE'
		/* The following line can be uncommented for symetric replication */
		/* and	upper(USER) <> upper('CHINOOK') */
		;
	end;


//
ALTER TRIGGER "CHINOOK"."T$GENRE" ENABLE;
