n0:createOracleTrigger.n0:source:
CREATE OR REPLACE EDITIONABLE TRIGGER "CHINOOK"."T$INVOICE" 
	after insert or update or delete on CHINOOK.INVOICE
	for each row
	declare
		V_FLAG	VARCHAR(1);
		V_INVOICEID	NUMBER;
	begin
		if inserting then
			V_INVOICEID := :new.INVOICEID;
			V_FLAG := 'I';
		end if;

		if updating then
			V_INVOICEID := :new.INVOICEID;
			V_FLAG := 'I';
		end if;

		if deleting then
			V_INVOICEID := :old.INVOICEID;
			V_FLAG := 'D';
		end if;

		insert into CHINOOK.J$INVOICE
		(
			JRN_SUBSCRIBER,
			JRN_CONSUMED,
			JRN_FLAG,
			JRN_DATE,
			INVOICEID
		)
		select	JRN_SUBSCRIBER,
			'0',
			V_FLAG,
			sysdate,
			V_INVOICEID
		from	CHINOOK.SNP_SUBSCRIBERS
		where	JRN_TNAME = 'CHINOOK.INVOICE'
		/* The following line can be uncommented for symetric replication */
		/* and	upper(USER) <> upper('CHINOOK') */
		;
	end;


//
ALTER TRIGGER "CHINOOK"."T$INVOICE" ENABLE;
