n0:createOracleTrigger.n0:source:
CREATE OR REPLACE EDITIONABLE TRIGGER "CHINOOK"."T$INVOICELINE" 
	after insert or update or delete on CHINOOK.INVOICELINE
	for each row
	declare
		V_FLAG	VARCHAR(1);
		V_INVOICELINEID	NUMBER;
	begin
		if inserting then
			V_INVOICELINEID := :new.INVOICELINEID;
			V_FLAG := 'I';
		end if;

		if updating then
			V_INVOICELINEID := :new.INVOICELINEID;
			V_FLAG := 'I';
		end if;

		if deleting then
			V_INVOICELINEID := :old.INVOICELINEID;
			V_FLAG := 'D';
		end if;

		insert into CHINOOK.J$INVOICELINE
		(
			JRN_SUBSCRIBER,
			JRN_CONSUMED,
			JRN_FLAG,
			JRN_DATE,
			INVOICELINEID
		)
		select	JRN_SUBSCRIBER,
			'0',
			V_FLAG,
			sysdate,
			V_INVOICELINEID
		from	CHINOOK.SNP_SUBSCRIBERS
		where	JRN_TNAME = 'CHINOOK.INVOICELINE'
		/* The following line can be uncommented for symetric replication */
		/* and	upper(USER) <> upper('CHINOOK') */
		;
	end;


//
ALTER TRIGGER "CHINOOK"."T$INVOICELINE" ENABLE;
