n0:createOracleTrigger.n0:source:
CREATE OR REPLACE EDITIONABLE TRIGGER "CHINOOK"."T$MEDIATYPE" 
	after insert or update or delete on CHINOOK.MEDIATYPE
	for each row
	declare
		V_FLAG	VARCHAR(1);
		V_MEDIATYPEID	NUMBER;
	begin
		if inserting then
			V_MEDIATYPEID := :new.MEDIATYPEID;
			V_FLAG := 'I';
		end if;

		if updating then
			V_MEDIATYPEID := :new.MEDIATYPEID;
			V_FLAG := 'I';
		end if;

		if deleting then
			V_MEDIATYPEID := :old.MEDIATYPEID;
			V_FLAG := 'D';
		end if;

		insert into CHINOOK.J$MEDIATYPE
		(
			JRN_SUBSCRIBER,
			JRN_CONSUMED,
			JRN_FLAG,
			JRN_DATE,
			MEDIATYPEID
		)
		select	JRN_SUBSCRIBER,
			'0',
			V_FLAG,
			sysdate,
			V_MEDIATYPEID
		from	CHINOOK.SNP_SUBSCRIBERS
		where	JRN_TNAME = 'CHINOOK.MEDIATYPE'
		/* The following line can be uncommented for symetric replication */
		/* and	upper(USER) <> upper('CHINOOK') */
		;
	end;


//
ALTER TRIGGER "CHINOOK"."T$MEDIATYPE" ENABLE;
