n0:createOracleTrigger.n0:source:
CREATE OR REPLACE EDITIONABLE TRIGGER "CHINOOK"."T$PLAYLISTTRACK" 
	after insert or update or delete on CHINOOK.PLAYLISTTRACK
	for each row
	declare
		V_FLAG	VARCHAR(1);
		V_PLAYLISTID	NUMBER;
	V_TRACKID	NUMBER;
	begin
		if inserting then
			V_PLAYLISTID := :new.PLAYLISTID;
		V_TRACKID := :new.TRACKID;
			V_FLAG := 'I';
		end if;

		if updating then
			V_PLAYLISTID := :new.PLAYLISTID;
		V_TRACKID := :new.TRACKID;
			V_FLAG := 'I';
		end if;

		if deleting then
			V_PLAYLISTID := :old.PLAYLISTID;
		V_TRACKID := :old.TRACKID;
			V_FLAG := 'D';
		end if;

		insert into CHINOOK.J$PLAYLISTTRACK
		(
			JRN_SUBSCRIBER,
			JRN_CONSUMED,
			JRN_FLAG,
			JRN_DATE,
			PLAYLISTID,
		TRACKID
		)
		select	JRN_SUBSCRIBER,
			'0',
			V_FLAG,
			sysdate,
			V_PLAYLISTID,
		V_TRACKID
		from	CHINOOK.SNP_SUBSCRIBERS
		where	JRN_TNAME = 'CHINOOK.PLAYLISTTRACK'
		/* The following line can be uncommented for symetric replication */
		/* and	upper(USER) <> upper('CHINOOK') */
		;
	end;


//
ALTER TRIGGER "CHINOOK"."T$PLAYLISTTRACK" ENABLE;
