n0:createOracleTrigger.n0:source:
CREATE OR REPLACE EDITIONABLE TRIGGER "CHINOOK"."T$TRACK" 
	after insert or update or delete on CHINOOK.TRACK
	for each row
	declare
		V_FLAG	VARCHAR(1);
		V_TRACKID	NUMBER;
	begin
		if inserting then
			V_TRACKID := :new.TRACKID;
			V_FLAG := 'I';
		end if;

		if updating then
			V_TRACKID := :new.TRACKID;
			V_FLAG := 'I';
		end if;

		if deleting then
			V_TRACKID := :old.TRACKID;
			V_FLAG := 'D';
		end if;

		insert into CHINOOK.J$TRACK
		(
			JRN_SUBSCRIBER,
			JRN_CONSUMED,
			JRN_FLAG,
			JRN_DATE,
			TRACKID
		)
		select	JRN_SUBSCRIBER,
			'0',
			V_FLAG,
			sysdate,
			V_TRACKID
		from	CHINOOK.SNP_SUBSCRIBERS
		where	JRN_TNAME = 'CHINOOK.TRACK'
		/* The following line can be uncommented for symetric replication */
		/* and	upper(USER) <> upper('CHINOOK') */
		;
	end;


//
ALTER TRIGGER "CHINOOK"."T$TRACK" ENABLE;
